-- Create a view onto q_audit_step formatted as q_audit_step_v1.-- If q_audit_step changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_q_audit_step_v1.DROP VIEW IF EXISTS types_plus.q_audit_step_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.q_audit_step_v1 AS select id,        hsys_id,        qsub_type_id_audit,        step,        marked_for_deletion,        last_updated_by_data_file_id,        created_dts,        updated_dts,        created_by,        updated_by,        sort,        qsub_type_id_event   from q_audit_step;ALTER TABLE types_plus.q_audit_step_v1    OWNER TO user_change_structure;-- Create a casting function to convert q_audit_step rows into the compound type format q_audit_step_v1.-- If q_audit_step changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.q_audit_step_to_v1 (q_audit_step_in q_audit_step)  RETURNS q_audit_step_v1AS $BODY$                   SELECT			q_audit_step_in.id,			q_audit_step_in.hsys_id,			q_audit_step_in.qsub_type_id_audit,			q_audit_step_in.step,			q_audit_step_in.marked_for_deletion,			q_audit_step_in.last_updated_by_data_file_id,			q_audit_step_in.created_dts,			q_audit_step_in.updated_dts,			q_audit_step_in.created_by,			q_audit_step_in.updated_by,			q_audit_step_in.sort,			q_audit_step_in.qsub_type_id_event               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.q_audit_step_to_v1 (q_audit_step_in q_audit_step)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert q_audit_step rows into the q_audit_step_v1 compound type format using the following syntax:-- select q_audit_step::q_audit_step_v1 from q_audit_stepDROP CAST IF EXISTS (q_audit_step as q_audit_step_v1);CREATE CAST (q_audit_step as q_audit_step_v1) WITH FUNCTION types_plus.q_audit_step_to_v1(q_audit_step);-- Create a function to accept an array of rows formatted as q_audit_step_v1 for UPSERT into q_audit_step.DROP FUNCTION IF EXISTS types_plus.insert_q_audit_step_v1 (types_plus.q_audit_step_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_q_audit_step_v1 (data_in types_plus.q_audit_step_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO q_audit_step (			id,			hsys_id,			qsub_type_id_audit,			step,			marked_for_deletion,			last_updated_by_data_file_id,			created_dts,			updated_dts,			created_by,			updated_by,			sort,			qsub_type_id_event)                  SELECT			rows_in.id,			rows_in.hsys_id,			rows_in.qsub_type_id_audit,			rows_in.step,			rows_in.marked_for_deletion,			rows_in.last_updated_by_data_file_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.created_by,			rows_in.updated_by,			rows_in.sort,			rows_in.qsub_type_id_event                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			hsys_id = EXCLUDED.hsys_id,			qsub_type_id_audit = EXCLUDED.qsub_type_id_audit,			step = EXCLUDED.step,			marked_for_deletion = EXCLUDED.marked_for_deletion,			last_updated_by_data_file_id = EXCLUDED.last_updated_by_data_file_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by,			sort = EXCLUDED.sort,			qsub_type_id_event = EXCLUDED.qsub_type_id_event          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_q_audit_step_v1(types_plus.q_audit_step_v1[])	OWNER TO user_bender;