-- Create a casting function to convert q_level rows into the compound type format q_level_v1.-- If q_level changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.q_level_to_v1 (q_level_in q_level)  RETURNS q_level_v1AS $BODY$                   SELECT			q_level_in.id,			q_level_in.hsys_id,			q_level_in.last_updated_by_data_file_id,			q_level_in.created_dts,			q_level_in.updated_dts,			q_level_in.level_,			q_level_in.marked_for_deletion,			q_level_in.created_by,			q_level_in.updated_by,			q_level_in.name_,			q_level_in.ascendco_name               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.q_level_to_v1 (q_level_in q_level)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert q_level rows into the q_level_v1 compound type format using the following syntax:-- select q_level::q_level_v1 from q_levelDROP CAST IF EXISTS (q_level as q_level_v1);CREATE CAST (q_level as q_level_v1) WITH FUNCTION types_plus.q_level_to_v1(q_level);