-- Create a casting function to convert q_type rows into the compound type format q_type_v1.-- If q_type changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.q_type_to_v1 (q_type_in q_type)  RETURNS q_type_v1AS $BODY$                   SELECT			q_type_in.id,			q_type_in.hsys_id,			q_type_in.last_updated_by_data_file_id,			q_type_in.created_dts,			q_type_in.updated_dts,			q_type_in.marked_for_deletion,			q_type_in.is_active,			q_type_in.updated_by,			q_type_in.created_by,			q_type_in.ascendco_name,			q_type_in.name_,			q_type_in.type_               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.q_type_to_v1 (q_type_in q_type)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert q_type rows into the q_type_v1 compound type format using the following syntax:-- select q_type::q_type_v1 from q_typeDROP CAST IF EXISTS (q_type as q_type_v1);CREATE CAST (q_type as q_type_v1) WITH FUNCTION types_plus.q_type_to_v1(q_type);