-- Create a casting function to convert rep rows into the compound type format rep_v1.-- If rep changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.rep_to_v1 (rep_in rep)  RETURNS rep_v1AS $BODY$                   SELECT			rep_in.id,			rep_in.user_id,			rep_in.hsys_id,			rep_in.sup_id,			rep_in.created_dts,			rep_in.updated_dts,			rep_in.marked_for_deletion,			rep_in.name_,			rep_in.phone,			rep_in.email,			rep_in.created_by,			rep_in.updated_by               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.rep_to_v1 (rep_in rep)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert rep rows into the rep_v1 compound type format using the following syntax:-- select rep::rep_v1 from repDROP CAST IF EXISTS (rep as rep_v1);CREATE CAST (rep as rep_v1) WITH FUNCTION types_plus.rep_to_v1(rep);