-- Create a function to accept an array of rows formatted as specialty_v1 for UPSERT into specialty.DROP FUNCTION IF EXISTS types_plus.insert_specialty_v1 (types_plus.specialty_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_specialty_v1 (data_in types_plus.specialty_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO specialty (			id,			data_file_id,			marked_for_deletion,			name_,			name_short)        SELECT			rows_in.id,			rows_in.data_file_id,			rows_in.marked_for_deletion,			rows_in.name_,			rows_in.name_short        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			data_file_id = EXCLUDED.data_file_id,			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_,			name_short = EXCLUDED.name_short        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_specialty_v1(types_plus.specialty_v1[]) OWNER TO user_bender;