-- Create a casting function to convert sterilize_method rows into the compound type format sterilize_method_v1.-- If sterilize_method changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sterilize_method_to_v1 (sterilize_method_in sterilize_method)  RETURNS sterilize_method_v1AS $BODY$        SELECT			sterilize_method_in.id,			sterilize_method_in.data_file_id,			sterilize_method_in.marked_for_deletion,			sterilize_method_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.sterilize_method_to_v1 (sterilize_method_in sterilize_method) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sterilize_method rows into the sterilize_method_v1 compound type format using the following syntax:-- select sterilize_method::sterilize_method_v1 from sterilize_methodDROP CAST IF EXISTS (sterilize_method as sterilize_method_v1);CREATE CAST (sterilize_method as sterilize_method_v1) WITH FUNCTION types_plus.sterilize_method_to_v1(sterilize_method);