-- Create a function to accept an array of rows formatted as sterilize_method_v1 for UPSERT into sterilize_method.DROP FUNCTION IF EXISTS types_plus.insert_sterilize_method_v1 (types_plus.sterilize_method_v1[]);CREATE OR REPLACE FUNCTION types_plus.insert_sterilize_method_v1 (data_in types_plus.sterilize_method_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO sterilize_method (			id,			data_file_id,			marked_for_deletion,			name_)        SELECT			rows_in.id,			rows_in.data_file_id,			rows_in.marked_for_deletion,			rows_in.name_        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			data_file_id = EXCLUDED.data_file_id,			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_sterilize_method_v1(types_plus.sterilize_method_v1[]) OWNER TO user_bender;