-- Create a casting function to convert sterilize_params rows into the compound type format sterilize_params_v1.-- If sterilize_params changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sterilize_params_to_v1 (sterilize_params_in sterilize_params)  RETURNS sterilize_params_v1AS $BODY$        SELECT			sterilize_params_in.id,			sterilize_params_in.data_file_id,			sterilize_params_in.marked_for_deletion,			sterilize_params_in.hsys_id,			sterilize_params_in.sterilize_method_id,			sterilize_params_in.name_$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.sterilize_params_to_v1 (sterilize_params_in sterilize_params) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sterilize_params rows into the sterilize_params_v1 compound type format using the following syntax:-- select sterilize_params::sterilize_params_v1 from sterilize_paramsDROP CAST IF EXISTS (sterilize_params as sterilize_params_v1);CREATE CAST (sterilize_params as sterilize_params_v1) WITH FUNCTION types_plus.sterilize_params_to_v1(sterilize_params);