-- Create a view onto sterilizer_load formatted as sterilizer_load_v1.-- If sterilizer_load changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_sterilizer_load_v1.DROP VIEW IF EXISTS types_plus.sterilizer_load_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.sterilizer_load_v1 AS select id,        data_file_id,        marked_for_deletion,        sterilize_method_id,        sterilizer_id,        sterilize_params_id,        created_dts,        load_no,        status_building_dts,        status_sterilizing_dts,        status_cooling_dts,        status_done_dts,        notes,        failed_notes,        utcoffset_seconds   from sterilizer_load;ALTER TABLE types_plus.sterilizer_load_v1    OWNER TO user_change_structure;