-- Create a view onto sterilizer_load formatted as sterilizer_load_v2.-- If sterilizer_load changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_sterilizer_load_v2.DROP VIEW IF EXISTS types_plus.sterilizer_load_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.sterilizer_load_v2 AS select id,        data_file_id,        marked_for_deletion,        sterilize_method_id,        sterilizer_id,        sterilize_params_id,        created_dts,        load_no,        status_building_dts,        status_sterilizing_dts,        status_cooling_dts,        status_done_dts,        notes,        failed_notes,        utcoffset_seconds   from sterilizer_load;ALTER TABLE types_plus.sterilizer_load_v2    OWNER TO user_change_structure;-- Create a casting function to convert sterilizer_load rows into the compound type format sterilizer_load_v2.-- If sterilizer_load changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sterilizer_load_to_v2 (sterilizer_load_in sterilizer_load)  RETURNS sterilizer_load_v2AS $BODY$        SELECT			sterilizer_load_in.id,			sterilizer_load_in.data_file_id,			sterilizer_load_in.marked_for_deletion,			sterilizer_load_in.sterilize_method_id,			sterilizer_load_in.sterilizer_id,			sterilizer_load_in.sterilize_params_id,			sterilizer_load_in.created_dts,			sterilizer_load_in.load_no,			sterilizer_load_in.status_building_dts,			sterilizer_load_in.status_sterilizing_dts,			sterilizer_load_in.status_cooling_dts,			sterilizer_load_in.status_done_dts,			sterilizer_load_in.notes,			sterilizer_load_in.failed_notes,			sterilizer_load_in.utcoffset_seconds$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.sterilizer_load_to_v2 (sterilizer_load_in sterilizer_load) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sterilizer_load rows into the sterilizer_load_v2 compound type format using the following syntax:-- select sterilizer_load::sterilizer_load_v2 from sterilizer_loadDROP CAST IF EXISTS (sterilizer_load as sterilizer_load_v2);CREATE CAST (sterilizer_load as sterilizer_load_v2) WITH FUNCTION types_plus.sterilizer_load_to_v2(sterilizer_load);-- Create a function to accept an array of rows formatted as sterilizer_load_v2 for UPSERT into sterilizer_load.DROP FUNCTION IF EXISTS types_plus.insert_sterilizer_load_v2 (types_plus.sterilizer_load_v2[]);CREATE OR REPLACE FUNCTION types_plus.insert_sterilizer_load_v2 (data_in types_plus.sterilizer_load_v2[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO sterilizer_load (			id,			data_file_id,			marked_for_deletion,			sterilize_method_id,			sterilizer_id,			sterilize_params_id,			created_dts,			load_no,			status_building_dts,			status_sterilizing_dts,			status_cooling_dts,			status_done_dts,			notes,			failed_notes,			utcoffset_seconds)        SELECT			rows_in.id,			rows_in.data_file_id,			rows_in.marked_for_deletion,			rows_in.sterilize_method_id,			rows_in.sterilizer_id,			rows_in.sterilize_params_id,			rows_in.created_dts,			rows_in.load_no,			rows_in.status_building_dts,			rows_in.status_sterilizing_dts,			rows_in.status_cooling_dts,			rows_in.status_done_dts,			rows_in.notes,			rows_in.failed_notes,			rows_in.utcoffset_seconds        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			data_file_id = EXCLUDED.data_file_id,			marked_for_deletion = EXCLUDED.marked_for_deletion,			sterilize_method_id = EXCLUDED.sterilize_method_id,			sterilizer_id = EXCLUDED.sterilizer_id,			sterilize_params_id = EXCLUDED.sterilize_params_id,			created_dts = EXCLUDED.created_dts,			load_no = EXCLUDED.load_no,			status_building_dts = EXCLUDED.status_building_dts,			status_sterilizing_dts = EXCLUDED.status_sterilizing_dts,			status_cooling_dts = EXCLUDED.status_cooling_dts,			status_done_dts = EXCLUDED.status_done_dts,			notes = EXCLUDED.notes,			failed_notes = EXCLUDED.failed_notes,			utcoffset_seconds = EXCLUDED.utcoffset_seconds        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.insert_sterilizer_load_v2(types_plus.sterilizer_load_v2[]) OWNER TO user_bender;