-- Create a casting function to convert sterilizer_log rows into the compound type format sterilizer_log_v1.-- If sterilizer_log changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sterilizer_log_to_v1 (sterilizer_log_in sterilizer_log)  RETURNS sterilizer_log_v1AS $BODY$        SELECT			sterilizer_log_in.id,			sterilizer_log_in.data_file_id,			sterilizer_log_in.log_type,			sterilizer_log_in.summary_json,			sterilizer_log_in.log_xml$BODY$LANGUAGE sql;ALTER FUNCTION types_plus.sterilizer_log_to_v1 (sterilizer_log_in sterilizer_log) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sterilizer_log rows into the sterilizer_log_v1 compound type format using the following syntax:-- select sterilizer_log::sterilizer_log_v1 from sterilizer_logDROP CAST IF EXISTS (sterilizer_log as sterilizer_log_v1);CREATE CAST (sterilizer_log as sterilizer_log_v1) WITH FUNCTION types_plus.sterilizer_log_to_v1(sterilizer_log);