-- Create a casting function to convert sterilizer rows into the compound type format sterilizer_v1.-- If sterilizer changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sterilizer_to_v1 (sterilizer_in sterilizer)  RETURNS sterilizer_v1AS $BODY$                   SELECT			sterilizer_in.id,			sterilizer_in.data_file_id,			sterilizer_in.marked_for_deletion,			sterilizer_in.facility_id,			sterilizer_in.sterilize_method_id,			sterilizer_in.name_               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.sterilizer_to_v1 (sterilizer_in sterilizer) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sterilizer rows into the sterilizer_v1 compound type format using the following syntax:-- select sterilizer::sterilizer_v1 from sterilizerDROP CAST IF EXISTS (sterilizer as sterilizer_v1);CREATE CAST (sterilizer as sterilizer_v1) WITH FUNCTION types_plus.sterilizer_to_v1(sterilizer);