-- Create a casting function to convert sup_parent rows into the compound type format sup_parent_v1.-- If sup_parent changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sup_parent_to_v1 (sup_parent_in sup_parent)  RETURNS sup_parent_v1AS $BODY$                   SELECT			sup_parent_in.id,			sup_parent_in.created_dts,			sup_parent_in.updated_dts,			sup_parent_in.marked_for_deletion,			sup_parent_in.name_,			sup_parent_in.created_by,			sup_parent_in.updated_by               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.sup_parent_to_v1 (sup_parent_in sup_parent)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sup_parent rows into the sup_parent_v1 compound type format using the following syntax:-- select sup_parent::sup_parent_v1 from sup_parentDROP CAST IF EXISTS (sup_parent as sup_parent_v1);CREATE CAST (sup_parent as sup_parent_v1) WITH FUNCTION types_plus.sup_parent_to_v1(sup_parent);