-- Create a view onto sup_parent formatted as sup_parent_v1.-- If sup_parent changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_sup_parent_v1.DROP VIEW IF EXISTS types_plus.sup_parent_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.sup_parent_v1 AS select id,        created_dts,        updated_dts,        marked_for_deletion,        name_,        created_by,        updated_by   from sup_parent;ALTER TABLE types_plus.sup_parent_v1    OWNER TO user_change_structure;-- Create a casting function to convert sup_parent rows into the compound type format sup_parent_v1.-- If sup_parent changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sup_parent_to_v1 (sup_parent_in sup_parent)  RETURNS sup_parent_v1AS $BODY$                   SELECT			sup_parent_in.id,			sup_parent_in.created_dts,			sup_parent_in.updated_dts,			sup_parent_in.marked_for_deletion,			sup_parent_in.name_,			sup_parent_in.created_by,			sup_parent_in.updated_by               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.sup_parent_to_v1 (sup_parent_in sup_parent)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sup_parent rows into the sup_parent_v1 compound type format using the following syntax:-- select sup_parent::sup_parent_v1 from sup_parentDROP CAST IF EXISTS (sup_parent as sup_parent_v1);CREATE CAST (sup_parent as sup_parent_v1) WITH FUNCTION types_plus.sup_parent_to_v1(sup_parent);-- Create a function to accept an array of rows formatted as sup_parent_v1 for UPSERT into sup_parent.DROP FUNCTION IF EXISTS types_plus.insert_sup_parent_v1 (types_plus.sup_parent_v1[]);  CREATE OR REPLACE FUNCTION types_plus.insert_sup_parent_v1 (data_in types_plus.sup_parent_v1[])  RETURNS intAS $BODY$ -- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO sup_parent (			id,			created_dts,			updated_dts,			marked_for_deletion,			name_,			created_by,			updated_by)                  SELECT			rows_in.id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.marked_for_deletion,			rows_in.name_,			rows_in.created_by,			rows_in.updated_by                      FROM unnest(data_in) as rows_in                  ON CONFLICT(id) DO UPDATE SET			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			marked_for_deletion = EXCLUDED.marked_for_deletion,			name_ = EXCLUDED.name_,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by          returning 1 as row_counter)         select sum(row_counter)::integer from inserted_rows; $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.insert_sup_parent_v1(types_plus.sup_parent_v1[])	OWNER TO user_bender;