-- Create a casting function to convert sup rows into the compound type format sup_v2.-- If sup changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.sup_to_v2 (sup_in sup)  RETURNS sup_v2AS $BODY$                   SELECT			sup_in.id,			sup_in.custom_facility_id,			sup_in.sup_parent_id,			sup_in.created_dts,			sup_in.updated_dts,			sup_in.is_custom_by_facility,			sup_in.marked_for_deletion,			sup_in.is_available_for_loaners,			sup_in.name_,			sup_in.created_by,			sup_in.updated_by,			sup_in.short_name               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.sup_to_v2 (sup_in sup)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert sup rows into the sup_v2 compound type format using the following syntax:-- select sup::sup_v2 from supDROP CAST IF EXISTS (sup as sup_v2);CREATE CAST (sup as sup_v2) WITH FUNCTION types_plus.sup_to_v2(sup);