-- Create a view onto assembly_prods formatted as assembly_prods_v1.-- If assembly_prods changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See ascendco.insert_assembly_prods_v1.DROP VIEW IF EXISTS types_plus.assembly_prods_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.assembly_prods_v1 AS select id,        data_file_id,        marked_for_deletion,        assembly_id,        item_prod_id,        last_updated_by_data_file_id,        created_dts,        updated_dts,        target,        found_,        repair,        backup,        actual,        con_id,        assembly_pos,        created_by,        updated_by   from assembly_prods;ALTER TABLE types_plus.assembly_prods_v1    OWNER TO user_change_structure;-- Create a casting function to convert assembly_prods rows into the compound type format assembly_prods_v1.-- If assembly_prods changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION ascendco.assembly_prods_to_v1 (assembly_prods_in assembly_prods)  RETURNS assembly_prods_v1AS $BODY$        SELECT			assembly_prods_in.id,			assembly_prods_in.data_file_id,			assembly_prods_in.marked_for_deletion,			assembly_prods_in.assembly_id,			assembly_prods_in.item_prod_id,			assembly_prods_in.last_updated_by_data_file_id,			assembly_prods_in.created_dts,			assembly_prods_in.updated_dts,			assembly_prods_in.target,			assembly_prods_in.found_,			assembly_prods_in.repair,			assembly_prods_in.backup,			assembly_prods_in.actual,			assembly_prods_in.con_id,			assembly_prods_in.assembly_pos,			assembly_prods_in.created_by,			assembly_prods_in.updated_by$BODY$LANGUAGE sql;ALTER FUNCTION ascendco.assembly_prods_to_v1 (assembly_prods_in assembly_prods) OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert assembly_prods rows into the assembly_prods_v1 compound type format using the following syntax:-- select assembly_prods::assembly_prods_v1 from assembly_prodsDROP CAST IF EXISTS (assembly_prods as assembly_prods_v1);CREATE CAST (assembly_prods as assembly_prods_v1) WITH FUNCTION ascendco.assembly_prods_to_v1(assembly_prods);-- Create a function to accept an array of rows formatted as assembly_prods_v1 for UPSERT into assembly_prods.DROP FUNCTION IF EXISTS ascendco.insert_assembly_prods_v1 (types_plus.assembly_prods_v1[]);CREATE OR REPLACE FUNCTION ascendco.insert_assembly_prods_v1 (data_in types_plus.assembly_prods_v1[])  RETURNS intAS $BODY$-- The CTE below is a roundabout way of returning an insertion count from a pure SQL function in Postgres.with inserted_rows as (        INSERT INTO assembly_prods (			id,			data_file_id,			marked_for_deletion,			assembly_id,			item_prod_id,			last_updated_by_data_file_id,			created_dts,			updated_dts,			target,			found_,			repair,			backup,			actual,			con_id,			assembly_pos,			created_by,			updated_by)        SELECT			rows_in.id,			rows_in.data_file_id,			rows_in.marked_for_deletion,			rows_in.assembly_id,			rows_in.item_prod_id,			rows_in.last_updated_by_data_file_id,			rows_in.created_dts,			rows_in.updated_dts,			rows_in.target,			rows_in.found_,			rows_in.repair,			rows_in.backup,			rows_in.actual,			rows_in.con_id,			rows_in.assembly_pos,			rows_in.created_by,			rows_in.updated_by        FROM unnest(data_in) as rows_in        ON CONFLICT(id) DO UPDATE SET			data_file_id = EXCLUDED.data_file_id,			marked_for_deletion = EXCLUDED.marked_for_deletion,			assembly_id = EXCLUDED.assembly_id,			item_prod_id = EXCLUDED.item_prod_id,			last_updated_by_data_file_id = EXCLUDED.last_updated_by_data_file_id,			created_dts = EXCLUDED.created_dts,			updated_dts = EXCLUDED.updated_dts,			target = EXCLUDED.target,			found_ = EXCLUDED.found_,			repair = EXCLUDED.repair,			backup = EXCLUDED.backup,			actual = EXCLUDED.actual,			con_id = EXCLUDED.con_id,			assembly_pos = EXCLUDED.assembly_pos,			created_by = EXCLUDED.created_by,			updated_by = EXCLUDED.updated_by        returning 1 as row_counter)    select sum(row_counter)::integer from inserted_rows;$BODY$LANGUAGE sql;ALTER FUNCTION ascendco.insert_assembly_prods_v1(types_plus.assembly_prods_v1[]) OWNER TO user_bender;