-- Create a casting function to convert print_job rows into the compound type format print_job_v1.-- If print_job changes, you can update the casting to handle the modification smoothly.CREATE OR REPLACE FUNCTION types_plus.print_job_to_v1 (print_job_in print_job)  RETURNS print_job_v1AS $BODY$                   SELECT			print_job_in.id,			print_job_in.created_dts,			print_job_in.updated_dts,			print_job_in.about_to_close_print_job_dts,			print_job_in.processing_time_ms,			print_job_in.close_print_job_ms,			print_job_in.marked_for_deletion,			print_job_in.mode_,			print_job_in.type_,			print_job_in.status,			print_job_in.error_info,			print_job_in.created_by,			print_job_in.updated_by,			print_job_in.printer_name,			print_job_in.network_printer_name,			print_job_in.pdf_address,			print_job_in.server_name_               $BODY$LANGUAGE sql;  ALTER FUNCTION types_plus.print_job_to_v1 (print_job_in print_job)	OWNER TO user_bender;-- Create/recreate the CAST. Use this to convert print_job rows into the print_job_v1 compound type format using the following syntax:-- select print_job::print_job_v1 from print_jobDROP CAST IF EXISTS (print_job as print_job_v1);CREATE CAST (print_job as print_job_v1) WITH FUNCTION types_plus.print_job_to_v1(print_job);