-- Create a view onto print_job formatted as print_job_v1.-- If print_job changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_print_job_v1.DROP VIEW IF EXISTS types_plus.print_job_v1 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.print_job_v1 AS select id,        created_dts,        updated_dts,        about_to_close_print_job_dts,        processing_time_ms,        close_print_job_ms,        marked_for_deletion,        mode_,        type_,        status,        error_info,        created_by,        updated_by,        printer_name,        network_printer_name,        pdf_address,        server_name_   from print_job;ALTER TABLE types_plus.print_job_v1    OWNER TO user_change_structure;