-- Create a view onto analytic_work formatted as analytic_work_v2.-- If analytic_work changes, you can update the view to handle the modification smoothly.-- Note: This view is automatically a custom type, with an array type to match. See types_plus.insert_analytic_work_v2.DROP VIEW IF EXISTS types_plus.analytic_work_v2 CASCADE;-- Careful!CREATE OR REPLACE VIEW types_plus.analytic_work_v2 AS select id,        key_supplement,        hsys_id,        facility_id,        inv_id,        user_id,        activity_id,        assembly_id,        q_event_id,        scan_id,        scase_id,        scase_inv_id,        sterilizer_load_id,        sterilizer_loadinv_id,        start_dts,        end_dts,        start_local_dts,        end_local_dts,        duration,        missing_inst,        num_inst,        num_items,        points,        num_packs,        num_trays,        activity,        description,        marked_for_deletion   from analytic_work;ALTER TABLE types_plus.analytic_work_v2    OWNER TO user_change_structure;