DROP VIEW IF EXISTS api.target_count_plus;CREATE OR REPLACE VIEW api.target_count_plus AS    SELECT hsys.name_ as hsys_name,           qualified_name,           records_count,           count_date,           count_dts      FROM dba.target_countINNER JOIN hsys on hsys.id = target_count.hsys_id;ALTER VIEW api.target_count_plus    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','target_count_plus','target_count with hsys name lookup.');