CREATE OR REPLACE VIEW api.analytic_productivity_plus ASSELECT	analytic_productivity.id,    analytic_productivity.pg_con_id,    analytic_productivity.data_file_id,    analytic_productivity.marked_for_deletion,    analytic_productivity.hsys_id,    analytic_productivity.facility_id,    analytic_productivity.facility_location_id,    analytic_productivity.specialty_id,    analytic_productivity.item_type_id,    analytic_productivity.user_name,    analytic_productivity.inv_name,    analytic_productivity.item_name,    analytic_productivity.tray_or_pack,    analytic_productivity.num_inst,    analytic_productivity.assembly_minutes,    analytic_productivity.pause_minutes,    analytic_productivity.points,    analytic_productivity.points_per_hour,    analytic_productivity.assembly_hour,    analytic_productivity.pause_hour,    analytic_productivity.start_utc,    analytic_productivity.start_local,    analytic_productivity.end_utc,    analytic_productivity.end_local,    /* Lookup fields */    hsys.name_ AS hsys_name,    facility.name_ AS facility_name,    facility.tz_name,	facility_location.name_ AS Location_name,    specialty.name_ AS specialty_name,    item_type.name_ AS item_type_name,    data_file_info.server_name_,    inv_id,    item_id	FROM analytic_productivity	LEFT JOIN hsys               ON (analytic_productivity.hsys_id                = hsys.id)	LEFT JOIN facility           ON (analytic_productivity.facility_id            = facility.id)	LEFT JOIN facility_location  ON (analytic_productivity.facility_location_id   = facility_location.id)	LEFT JOIN specialty          ON (analytic_productivity.specialty_id           = specialty.id)	LEFT JOIN item_type          ON (analytic_productivity.item_type_id           = item_type.id)	LEFT JOIN data_file_info   	 ON (analytic_productivity.data_file_id           = data_file_info.id)    ;ALTER VIEW api.analytic_productivity_plus    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','analytic_productivity_plus','Analytic data, derived largely from [Assembly].');