CREATE OR REPLACE VIEW api.assembly_plus ASSELECT	assembly.id,	assembly.data_file_id,	data_file_info.server_name_,	assembly.created_by_user_id,	COALESCE(web_user_created.username,'') AS created_by_lookup,	assembly.created_by,	assembly.done_by_user_id,	COALESCE(web_user_done.username,'') AS done_by,	assembly.facility_location_id,	facility_location.name_ AS faciility_location,	assembly.inv_id,	assembly.last_updated_by_data_file_id,	assembly.updated_by_user_id,	assembly.marked_for_deletion,	assembly.is_complete,	assembly.is_quick_turn,	assembly.con_id,	assembly.difficulty,	assembly.quantity,	assembly.sequence_no,	assembly.num_inst,	assembly.points,	assembly.done_dts,	assembly.created_dts,	assembly.updated_dts,	assembly.missing_list,	assembly.priority,	assembly.status,	assembly.updated_byFROM assemblyLEFT JOIN web_user AS web_user_created on web_user_created.id = assembly.created_by_user_idLEFT JOIN web_user AS web_user_done    on web_user_done.id = assembly.done_by_user_idLEFT JOIN data_file_info on (data_file_info.id = assembly.data_file_id)LEFT JOIN facility_location on (facility_location.id = assembly.facility_location_id);ALTER VIEW api.assembly_plus    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','assembly_plus','Assembly data, with lookup values.');