CREATE OR REPLACE VIEW api.calendar_day_plus AS   select calendar.calendar_name,          calendar.week_starts_on,          calendar_year.fiscal_year_name,          calendar_year.fiscal_year_abbr,		  to_number(calendar_year.fiscal_year_abbr, 'fm0000') as fiscal_year_number, -- Will give weird results for things like 2019-2020, or 19/20          calendar_year.days_in_year,          calendar_year.weeks_in_year,          calendar_year.first_day_of_week_usa,          calendar_year.first_day_of_week_iso,          calendar_year.first_day_of_week_name,          calendar_year.week_start_offset,          calendar_day.*     from calendar_dayleft join calendar      ON calendar.id      = calendar_day.calendar_idleft join calendar_year ON calendar_year.id = calendar_day.calendar_year_id;ALTER VIEW api.calendar_day_plus    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','calendar_day_plus','Calender date data, with the parent calendar name displayed.');