CREATE OR REPLACE VIEW api.push_log_domo AS SELECT 	push_log.id,	push_log.data_file_id,	data_file_info.server_name_,	push_log.ib_table_name,	push_log.ib_table_number,	push_log.records_count,	push_log.push_dts,	age(now(), push_log.push_dts) AS age,	minutes_old(push_log.push_dts) AS minutes_old,	data_file_info.app_name,	data_file_info.app_version,	data_file_info.shell_version,	data_file_info.sync_version,	data_file_info.iam_type,	data_file_info.compiled,	data_file_info.merged,	data_file_info.type_of_4d,	data_file_info.version_of_4d,	data_file_info.machine_name,	data_file_info.platform_description,	data_file_info.os_tz_name	FROM (ascendco.push_log	JOIN data_file_info ON ((data_file_info.id = push_log.data_file_id)))   ;ALTER VIEW api.push_log_domo    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','push_log_domo','The server, table name, record counts, and additional server details for every push into Postgres.','RDS push_log_domo');