CREATE OR REPLACE VIEW api.rcl_combined ASSELECT	record_changes_log.id,	record_changes_log.record_id,    record_changes_log.table_number,    record_changes_log.updated_dts,    record_changes_log.updated_by,    record_changes_log.app_type,    record_changes_log.machine_name,    record_changes_log_detail.id AS details_id,    record_changes_log_detail.field_name,    record_changes_log_detail.old_value,    record_changes_log_detail.new_valueFROM (record_changes_logJOIN record_changes_log_detail ON ((record_changes_log.id = record_changes_log_detail.record_changes_log_id)));ALTER VIEW api.rcl_combined    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','rcl_combined','Consolidated old:new details of a record_change_log and its items. Comparable to the originl [RecordChangesLog] in IceBerg/Sonar.');