CREATE OR REPLACE VIEW api.rcl_detail ASSELECT	 record_changes_log_detail.id,	 record_changes_log_detail.record_changes_log_id,  	 record_changes_log_detail.field_name, 	 record_changes_log_detail.old_value,	 record_changes_log_detail.new_value,	 record_changes_log_detail.record_idFROM record_changes_log_detail;ALTER VIEW api.rcl_detail    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','rcl_detail','The item (child) part of an rcl, showing a single field old:new.');