CREATE OR REPLACE VIEW api.stat_facility_domo AS SELECT stat_facility.id AS "ID",    stat_facility.date_ AS "Date",    stat_facility.category AS "Category",    stat_facility.item_type AS "ItemType",    stat_facility.stat_type AS "StatType",    stat_facility.is_most_recent AS "isMostRecent",    stat_facility.in_data_cleanse_scope AS "inDataCleanseScope",    COALESCE(hsys.name_, ''::citext) AS "HsysName",    COALESCE(facility.name_, ''::citext) AS "FacilityName",    stat_facility.items_inactive AS "Items_Inactive",    stat_facility.items_count AS "Items_Count",    stat_facility.items_qty AS "Items_Qty",    stat_facility.items_inventoried AS "Items_Inventoried",    stat_facility.items_to_inventory AS "Items_ToInventory",    stat_facility.items_named AS "Items_Named",    stat_facility.items_to_name AS "Items_ToName",    stat_facility.inst_qty AS "Inst_Qty",    stat_facility.items_has_loc AS "Items_HasLoc",    stat_facility.items_missing_locs AS "Items_MissingLocs",    stat_facility.item_prods_count AS "ItemProds_Count",    stat_facility.item_prods_missing_part_nos_thier AS "ItemProds_MissingPartNosThier",    stat_facility.item_prods_missing_vendors_thier AS "ItemProds_MissingVendorsThier",    stat_facility.item_prods_dr_pref AS "ItemProds_DrPref",    stat_facility.item_prods_critical AS "ItemProds_Critical",    stat_facility.item_prods_not_linked AS "ItemProds_NotLinked",    stat_facility.item_prods_missing_part_nos AS "ItemProds_MissingPartNos",    stat_facility.item_prods_missing_vendors AS "ItemProds_MissingVendors",    stat_facility.item_prods_cant_determine AS "ItemProds_CantDetermine",    stat_facility.item_prods_linked AS "ItemProds_Linked",    stat_facility.sup_prods_count AS "SupProds_Count",    stat_facility.sups_count AS "Sups_Count",    stat_facility.prods_count AS "Prods_Count",    stat_facility.prods_with_pic AS "Prods_WithPic",    stat_facility.prods_without_pic AS "Prods_WithoutPic",    stat_facility.item_prods_verified AS "ItemProds_Verified",    stat_facility.item_prods_expert_linked AS "ItemProds_ExpertLinked",    stat_facility.item_prods_dup_names_their AS "ItemProds_DupNamesTheir",    stat_facility.item_prods_dup_names AS "ItemProds_DupNames",    stat_facility.prods_named AS "Prods_Named",    stat_facility.part_nos_fixed AS "PartNos_Fixed",    stat_facility.invs_inactive AS "Invs_Inactive",    stat_facility.invs_count AS "Invs_Count",    stat_facility.invs_found AS "Invs_Found",    stat_facility.emr_items_count AS "EmrItems_Count",    stat_facility.emr_items_linked AS "EmrItems_Linked",    stat_facility.emr_items_matches_their_name AS "EmrItems_MatchesTheirName",    stat_facility.emr_items_matches_our_name AS "EmrItems_MatchesOurName",    stat_facility.emr_items_no_match_their_name AS "EmrItems_NoMatchTheirName",    stat_facility.emr_items_no_match_our_name AS "EmrItems_NoMatchOurName",    stat_facility.emr_items_dups AS "EmrItems_Dups"    FROM stat_facility	LEFT JOIN hsys 		ON (stat_facility.hsys_id     = hsys.id)	LEFT JOIN facility	ON (stat_facility.facility_id = facility.id) ;ALTER VIEW api.stat_facility_domo    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','stat_facility_domo','[Stat_Facility] data.','PROD Stat Facility');