CREATE OR REPLACE VIEW api.views ASSELECT schema_name,       view_name,       domo_dataset,       description  FROM dba.view_catalog;ALTER VIEW api.views    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','views','You''re looking at it! This is the public face of dba.view_catalog. Not all views are listed, only ones of interest.');