CREATE OR REPLACE VIEW ascendco.data_file_info_psql ASSELECT	data_file_info.last_updated_dts,    age(now(), data_file_info.last_updated_dts) AS age,    minutes_old(last_updated_dts) AS minutes_old,    data_file_info.os_tz_name,    data_file_info.server_name_,    data_file_info.app_name,    data_file_info.app_version,    data_file_info.shell_version,    data_file_info.sync_version,    data_file_info.compiled,    data_file_info.merged,    data_file_info.iam_type,    data_file_info.type_of_4d,    data_file_info.version_of_4d,    data_file_info.machine_name,    data_file_info.platform_description,    data_file_info.id,    data_file_info.iam,    data_file_info.table_statsFROM data_file_info;ALTER VIEW ascendco.data_file_info_psql	OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','data_file_info_psql','Extract the Postgres details from each machine''s IAM settings.');