CREATE OR REPLACE VIEW ascendco.clinic_plus AS   SELECT clinic.id,          clinic.name_,          clinic.receive_at_clinic,          coalesce (hsys.name_, '')  AS hsys_name,          clinic.marked_for_deletion,          clinic.created_dts,          clinic.updated_dts,          clinic.created_by,          clinic.updated_by     FROM clinicLEFT JOIN hsys     ON hsys.id     = clinic.hsys_id;ALTER VIEW ascendco.clinic_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','clinic_plus','clinc data, with lookup values.');