CREATE OR REPLACE VIEW ascendco.data_file_info_pg_details AS     SELECT data_file_info.last_updated_dts,            age(now(), data_file_info.last_updated_dts) AS age,            minutes_old(last_updated_dts) AS minutes_old,            jsonb_path_query (data_file_info.iam,'$.organization') as organization,            jsonb_path_query (data_file_info.iam,'$.pgsql.host_name') as host_name,            jsonb_path_query (data_file_info.iam,'$.pgsql.database_name') as database_name,            jsonb_path_query (data_file_info.iam,'$.pgsql.use_ssh') as use_ssh,            jsonb_path_query (data_file_info.iam,'$.pgsql.app_type') as app_type,            jsonb_path_query (data_file_info.iam,'$.pgsql.use_new_config_system') as use_new_config_system,            jsonb_path_query (data_file_info.iam,'$.pgsql.go_full_diagnostic') as go_full_diagnostic,            jsonb_path_query (data_file_info.iam,'$.pgsql.log_errors_locally') as log_errors_locally,            jsonb_path_query (data_file_info.iam,'$.pgsql.maximum_push_size_kb') as maximum_push_size_kb,            jsonb_path_query (data_file_info.iam,'$.pgsql.display_timing_results') as display_timing_results,            data_file_info.os_tz_name,            data_file_info.server_name_,            data_file_info.app_name,            data_file_info.app_version,            data_file_info.shell_version,            data_file_info.sync_version,            data_file_info.compiled,            data_file_info.merged,            data_file_info.iam_type,            data_file_info.type_of_4d,            data_file_info.version_of_4d,            data_file_info.machine_name,            data_file_info.platform_description,            data_file_info.id,            data_file_info.iam,            data_file_info.table_stats       FROM data_file_info;ALTER VIEW ascendco.data_file_info_pg_details    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','data_file_info_pg_details','This view combines basic machine description details and extracted Postgres IAM settings into a searchable view.');