DROP VIEW IF EXISTS ascendco.domo_passthrough_plus;CREATE OR REPLACE VIEW ascendco.domo_passthrough_plus AS    SELECT domo_passthrough.id,	       domo_passthrough.source_id,	       domo_passthrough.key_supplement,	       hsys.name_ AS hsys_name,	       facility.name_ AS facility_name,	       domo_passthrough.dataset_name,	       domo_passthrough.view_version,	       domo_passthrough.created_dts,		   age(now(), domo_passthrough.created_dts) AS age,	       minutes_old(domo_passthrough.created_dts) AS minutes_old,		   domo_passthrough.from_dts,	       domo_passthrough.to_dts,	       domo_passthrough.data     FROM domo_passthroughLEFT JOIN hsys      ON (hsys.id     = domo_passthrough.hsys_id)LEFT JOIN facility  ON (facility.id = domo_passthrough.facility_id);ALTER VIEW ascendco.domo_passthrough_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','domo_passthrough_plus','domo_passthrough data, with lookup values.');