CREATE OR REPLACE VIEW ascendco.facility_activities_plus ASSELECT	facility_activities.id,         facility_activities.name_,         hsys.name_                      AS hsys_name,         facility.name_                  AS facility_name,         data_file_info.server_name_     AS server_name,         facility_activities.created_dts,         facility_activities.updated_dts,         facility_activities.marked_for_deletion,         facility_activities.created_by,         facility_activities.updated_by	 FROM facility_activitiesLEFT JOIN facility       ON facility_activities.facility_id = facility.idLEFT JOIN hsys           ON facility.hsys_id = hsys.idLEFT JOIN data_file_info ON data_file_info.id = facility_activities.last_updated_by_data_file_id ;ALTER VIEW ascendco.facility_activities_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','facility_activities_plus','facility_activities data, with lookup values');