CREATE OR REPLACE VIEW ascendco.focus_plus AS   SELECT		scase_id,		hsys_id,		hsys.name_ as hsys_name,		facility_id,		facility_name,		sched_dts,		or_name,		case_seq,		desc_,		status,		transport_from,		surgeon_name,		proc_name,		when_     FROM focusLEFT JOIN hsys     ON (hsys.id     = focus.hsys_id);ALTER VIEW ascendco.focus_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','focus_plus','focus data, with lookup values.');