CREATE OR REPLACE VIEW ascendco.item_arch_plus AS   SELECT item_arch.id,          item_arch.name_,          item_arch.type_,          coalesce(facility.name_,'') as facility_name,       -- clinic.name_ as clinic_name, -- nope, don't have ascendco.clinic yet          item_arch.created_dts,          item_arch.updated_dts     FROM item_archLEFT JOIN facility ON facility.id = item_arch.facility_id;ALTER VIEW ascendco.item_arch_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','item_arch_plus','item_arch data, with lookup values.');