CREATE OR REPLACE VIEW ascendco.outlier_change_plus AS   SELECT outlier_rule.id AS outlier_rule_id,          outlier_rule.schema_name,          outlier_rule.table_name,          outlier_rule.column_name,          outlier_change.id,          outlier_change.value_was,          outlier_change.set_to,          outlier_change.change_count,          outlier_change.last_changed_dts     FROM outlier_changeLEFT JOIN outlier_rule ON (outlier_change.outlier_rule_id = outlier_rule.id)    ;ALTER VIEW ascendco.outlier_change_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','outlier_change_plus','outlier_change data, with lookup values.');