CREATE OR REPLACE VIEW ascendco.outlier_rule_plus AS   SELECT id,          schema_name,          table_name,          column_name,          threshold,          set_to,          (SELECT count(*) FROM outlier_change WHERE outlier_rule_id = outlier_rule.id) AS change_rows    FROM  outlier_rule;ALTER VIEW ascendco.outlier_rule_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','outlier_rule_plus','outlier_rule data, with lookup values.');