/* Find duplicates like this:select distinct rcl_detail_hash_this.record_changes_log_id,        rcl_detail_hash_this.updated_dts,        count(*) as duplicates   from rcl_detail_hash_thisgroup by rcl_detail_hash_this.record_changes_log_id,         rcl_detail_hash_this.updated_dts,         hashtext(rcl_detail_hash_this::text)  having count(*) > 1 order by  rcl_detail_hash_this.updated_dts desc,   -- I'd like to know if the bug is still active 		    rcl_detail_hash_this.record_changes_log_id          */DROP VIEW IF EXISTS ascendco.rcl_detail_hash_this;CREATE OR REPLACE VIEW ascendco.rcl_detail_hash_this ASSELECT record_changes_log.updated_dts,		record_changes_log.updated_by,		record_changes_log_detail.id,		record_changes_log_detail.record_changes_log_id,		record_changes_log_detail.field_name,		record_changes_log_detail.old_value,		record_changes_log_detail.new_value,		record_changes_log_detail.record_idFROM record_changes_log_detailLEFT JOIN record_changes_log ON (record_changes_log.id = record_changes_log_detail.record_changes_log_id);ALTER VIEW ascendco.rcl_detail_hash_this    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('ascendco','rcl_detail_hash_this','Historical tool: Helped finding duplicate RCL IDs.');