CREATE OR REPLACE VIEW ascendco.rep_plus AS   SELECT rep.id,          rep.name_,          rep.phone,          rep.email,          coalesce (hsys.name_, '')  AS hsys_name,          coalesce (sup.name_,  '')  AS sup_name,          coalesce (web_user.name_full, '')  AS user_name,          rep.marked_for_deletion,          rep.created_dts,          rep.updated_dts     FROM repLEFT JOIN hsys     ON hsys.id     = rep.hsys_idLEFT JOIN sup      ON sup.id      = rep.sup_idLEFT JOIN web_user ON web_user.id = rep.user_id;ALTER VIEW ascendco.rep_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','rep_plus','rep data, with lookup values.');