CREATE OR REPLACE VIEW ascendco.stat_facility_plus ASSELECT	stat_facility.id,	date_,	category,	item_type,	stat_type,	is_most_recent,	in_data_cleanse_scope,	stat_facility.facility_id,	stat_facility.hsys_id,    COALESCE(hsys.name_,'') AS hsys_name,    COALESCE(facility.name_,'') AS facility_name,	items_inactive,	items_count,	items_qty,	items_inventoried,	items_to_inventory,	items_named,	items_to_name,	inst_qty,	items_has_loc,	items_missing_locs,	item_prods_count,	item_prods_missing_part_nos_thier,	item_prods_missing_vendors_thier,	item_prods_dr_pref,	item_prods_critical,	item_prods_not_linked,	item_prods_missing_part_nos,	item_prods_missing_vendors,	item_prods_cant_determine,	item_prods_linked,	sup_prods_count,	sups_count,	prods_count,	prods_with_pic,	prods_without_pic,	item_prods_verified,	item_prods_expert_linked,	item_prods_dup_names_their,	item_prods_dup_names,	prods_named,	part_nos_fixed,	invs_inactive,	invs_count,	invs_found,	emr_items_count,	emr_items_linked,	emr_items_matches_their_name,	emr_items_matches_our_name,	emr_items_no_match_their_name,	emr_items_no_match_our_name,	emr_items_dups	FROM  stat_facility	LEFT JOIN hsys 		ON (stat_facility.hsys_id     = hsys.id)	LEFT JOIN facility	ON (stat_facility.facility_id = facility.id) ;ALTER VIEW ascendco.stat_facility_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','stat_facility_plus','facility data, with lookup values');