DROP VIEW IF EXISTS ascendco.sup_plus;CREATE OR REPLACE VIEW ascendco.sup_plus AS   SELECT sup.id,          sup.name_,          coalesce(sup_parent.name_,'') AS parent_name,          sup.is_custom_by_facility,          coalesce(facility.name_,'')   AS facility_name,          sup.is_available_for_loaners,          sup.marked_for_deletion,          sup.created_by,          sup.updated_by,          sup.created_dts,          sup.updated_dts     FROM supLEFT JOIN facility   ON facility.id   = sup.custom_facility_idLEFT JOIN sup_parent ON sup_parent.id = sup.sup_parent_id;ALTER VIEW ascendco.sup_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','sup_plus','sup data, with lookup values.');