DROP VIEW IF EXISTS ascendco.text_collection_item_plus;CREATE OR REPLACE VIEW ascendco.text_collection_item_plus AS   SELECT text_collection_item.collection_id,          text_collection.collection_name,          text_collection_item.text,          text_collection_item.source_id,          coalesce(facility.name_,'') AS facility_name,          text_collection.added_dts,          text_collection.retain     FROM text_collection_itemLEFT JOIN text_collection ON (text_collection.id = text_collection_item.collection_id)LEFT JOIN facility ON (facility.id = text_collection.facility_id);ALTER VIEW ascendco.text_collection_item_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','text_collection_item_plus','text_collection_item data, with lookup values.');