DROP VIEW IF EXISTS ascendco.text_collection_plus;CREATE OR REPLACE VIEW ascendco.text_collection_plus AS   SELECT text_collection.id,          text_collection.collection_name,          coalesce(facility.name_,'') AS facility_name,          (select count(*) from text_collection_item where collection_id = text_collection.id) as items_count,          text_collection.added_dts,          text_collection.retain     FROM text_collectionLEFT JOIN facility ON (facility.id = text_collection.facility_id);ALTER VIEW ascendco.text_collection_plus    OWNER TO user_change_structure;----------------------------------------------- Register view---------------------------------------------CALL view_register ('ascendco','text_collection_plus','text_collection data, with lookup values.');