DROP VIEW IF EXISTS api.target_count_plus;CREATE VIEW api.target_count_plus ASselect coalesce(hsys.name_,'') AS hsys_name,       tc.qualified_name,       tc.records_count,       tc.count_date,       tc.count_dtsfrom target_count tcleft join hsys on hsys.id = tc.hsys_id;ALTER VIEW dba.test_failed_focus	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('api','target_count_plus','dba.target_count fields with lookup values.');