DROP VIEW IF EXISTS ascendco.count_rows; -- Previous schema location.DROP VIEW IF EXISTS dba.count_rows;CREATE OR REPLACE VIEW dba.count_rows AS  SELECT table_schema,         table_name,         count_rows(table_schema, table_name)   FROM information_schema.tables  WHERE  table_schema not in ('pg_catalog', 'information_schema') and         table_type='BASE TABLE';ALTER VIEW dba.count_rows    OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','count_rows','Counts the rows in each of our tables, including individual partitions. This is likely to be quite slow on RDS.');