DROP VIEW IF EXISTS dba.custom_function_list;CREATE OR REPLACE VIEW dba.custom_function_list ASSELECT	  id,          proc_oid,          input_signature,          function_name,          function_signature,          specific_name,          schema_oid,          schema_oid::regnamespace::text as schema_name,          owner_oid::regrole::text as owner_name,          language_oid,          pg_language.lanname,          return_type_oid,          is_security_definer,          runs_as,          fails_on_null_input,          returns_table,          function_kind_code,          function_acl,          method_body,          api_version,          published,          dirty,          dirty_created,          dirty_altered,          dirty_dropped     FROM custom_functionLEFT JOIN pg_language ON (pg_language.oid = custom_function.language_oid);ALTER VIEW dba.custom_function_list	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------