CREATE OR REPLACE VIEW dba.ddl_event_log_plus AS SELECT event_timestamp,		age(now(), event_timestamp) AS age,		schema_name,		classid::regclass::text as object_class,		object_type,		object_identity,		object_name,		command_tag,		address_args,		address_names,		in_extension,		trigger_event,		id     FROM dba.ddl_event_logLEFT JOIN pg_proc on (pg_proc.oid =  ddl_event_log.objid);ALTER VIEW dba.ddl_event_log_plus	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','ddl_event_log_plus','Displays information about each structure modification made to the server. Including add/modify/delete operations on tables, functions, and more.');