CREATE OR REPLACE VIEW dba.extension_list AS  SELECT name,         installed_version,         default_version,         case when installed_version = default_version then '' else 'Out of date' end as update_hint,         comment    FROM pg_available_extensions   WHERE installed_version is not nullORDER BY name;ALTER VIEW dba.extension_list	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','extension_list','Lists installed extensions, with hints where updates are needed.');