DROP VIEW IF EXISTS dba.event_trigger_list;CREATE OR REPLACE VIEW dba.event_trigger_list ASselect evtname as event_trigger_name,       evtevent as event_name,       evtowner::regrole as event_owner,       evtfoid::regproc as function_name,       case when evtenabled = 'A' then 'Always'            when evtenabled = 'D' then 'Disabled'            when evtenabled = 'O' then 'Origin and Local'            when evtenabled = 'R' then 'Replica'       end as session_replication_role_mode,       coalesce(evttags::text,'No TAG filter') as event_tags from pg_event_trigger;ALTER VIEW dba.event_trigger_list	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','event_trigger_list','Lists our global DDL event triggers.');