DROP VIEW IF EXISTS dba.role_settings_list;CREATE OR REPLACE VIEW dba.role_settings_list AS   select pg_database.datname,          pg_roles.rolname,          pg_db_role_setting.setconfig     from pg_db_role_settingleft join pg_database ON (pg_database.oid = pg_db_role_setting.setdatabase)left join pg_roles    ON (pg_roles.oid    = pg_db_role_setting.setrole);ALTER VIEW dba.role_settings_list	OWNER TO user_change_structure;-------------------------------------------------- Register view------------------------------------------------CALL view_register ('dba','role_settings_list','Displays default connection settings for each role, including search_path.)');